mapper.insert:
MERGE INTO URL_VISIT_STATISTICS_YEAR T
USING (
   SELECT   #{year} AS STCS_YEAR
         , #{month} AS SCTS_MONTH
         , #{date} AS SCTS_DATE
         , #{hour} AS STCS_HOUR
         , #{minute} AS STCS_MINUTE
         , #{second} AS STCS_SECOND
         , #{ip} AS IP
         , #{url} AS URL
         , #{mthd} AS MTHD
   FROM   DUAL
)
ON   (
         T.STCS_YEAR = #{year}
   AND      T.MTHD = #{mthd}
   AND      T.IP = #{ip}
   AND      T.URL = #{url}
)
WHEN MATCHED THEN
   UPDATE   SET
   VISIT_COUNT = VISIT_COUNT + 1
WHEN NOT MATCHED THEN
   INSERT (
      STCS_SECOND
      , URL
      , IP
      , VISIT_COUNT
      , STCS_YEAR
      , STCS_MONTH
      , STCS_DATE
      , STCS_HOUR
      , STCS_MINUTE
      , STCS_ID
      , MTHD
   )
   VALUES   (
      #{second}
      , #{url}
      , #{ip}
      , 1
      , #{year}
      , #{month}
      , #{date}
      , #{hour}
      , #{minute}
      , NEXTVALUE('ST', URL_VISIT_STATISTICS_YEAR_ID.NEXTVAL)
      , #{mthd}
   )

